import React, { Component } from 'react';
import { connect } from 'react-redux'
import { Field, reduxForm } from 'redux-form'
import _ from 'lodash'

import { getInfo } from '../actions'

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {showFrag: false} //気温の結果の表示に与えるstate
    this.onSubmit = this.onSubmit.bind(this)
  }

  renderField(field) {
    const { input, label, type, meta: { touched, error } } = field
    return (
      <div>
        <input {...input} placeholder={label} type={type} />
        {touched && error && <span>{error}</span>}
      </div>
    );
  }

  async onSubmit(value) {
    //console.log(value.city) //都市名が取得できているかの確認
    await this.props.getInfo(value.city)
    if (this.props.result.error_message !== '') {
      this.setState({showFrag: !this.state.showFrag}) //気温の結果の表示のstateを切り替える
    }
    //const weather_info = this.props.result.data
    //console.log(weather_info.weather[0])
    //console.log(`天気は${weather_info.weather[0].main}`) //天気は？
    //let temp = weather_info.main.temp - 273.15 //ケルビン温度を摂氏に変換
    //let new_temp = temp.toFixed(2); //小数点第２位以下を四捨五入
    //console.log(`気温は${new_temp}℃`) //気温は？
    //console.log(`湿度は${weather_info.main.humidity}％`) //湿度は？
    //console.log(`風速は${weather_info.wind.speed}m/s`)  //風速は？
  }

  renderMainWeather() {
    let weather_info = this.props.result.data
    //console.log(weather_info.name) //都市名が取得できているかの確認
      return _.map(weather_info.weather, data => (
      <div key={data.id}>
        <div>現在の{weather_info.name}の天気は{data.main}</div>
      </div>
      ))
  }

  renderTemp() {
    let weather_info =this.props.result.data
    //console.log(weather_info.main) //mainが取得できているか確認
    let temp_info = _.filter (weather_info.main, {});
    //console.log(temp_info[0]) //気温が取得できているか確認
    let temp_c = temp_info[0] -273.15 //ケルビン温度を摂氏に変換
    let new_temp_c = temp_c.toFixed(2) //小数点第２位以下を四捨五入
    //console.log(new_temp_c)
    const showFrag = this.state.showFrag;
    let temp_text = showFrag ? <div>現在の{weather_info.name}の気温は{new_temp_c}℃</div> : ''; //showFragのstateに応じて表示と非表示を切り替える
    return (<div>{temp_text}</div>)
  }

  renderError() {
    const e_msg = this.props.result.error_message //reducers/events.jsからerror_messageを受け取る
    //console.log(e_msg)
    return (<div>{e_msg}</div>)
  }

  render() {
    const { handleSubmit, pristine, submitting } = this.props //redux-formからpropsを受け取る
    return (
      <React.Fragment>
        <form onSubmit={handleSubmit(this.onSubmit)}>
          <div><Field label="都市名を入力してください" name="city" type="text" component={this.renderField} /></div>
          <div>
            <input type="submit" value="Submit" disabled={pristine || submitting} />
          </div>
        </form>
        <div>{this.renderMainWeather()}</div>
        <div>{this.renderTemp()}</div>
        <div>{this.renderError()}</div>
      </React.Fragment>
    );
  }
}

const validate = values => {
  const errors = {}

  if (!values.city) errors.city = "都市名を入力してください"
  return errors
}

const mapStateToProps = state => ({result: state.result })
const mapDispatchToProps = ({ getInfo })

export default connect(mapStateToProps, mapDispatchToProps)(
  reduxForm({ validate, form: 'WeatherForm' })(App)
)
