import React, { Component } from 'react';
import { connect } from 'react-redux'
import { Field, reduxForm } from 'redux-form'
import { Link } from 'react-router-dom';

import { getInfo } from '../actions'

class EventsNew extends Component {
  constructor(props) {
    super(props)
    this.onSubmit = this.onSubmit.bind(this)
  }

  renderField(field) {
    const { input, label, type, meta: { touched, error } } = field
    return (
      <div>
        <input {...input} placeholder={label} type={type} />
        {touched && error && <span>{error}</span>}
      </div>
    );
  }

  async onSubmit(value) {
    console.log(value.city)
    await this.props.getInfo(value.city)
    const weather_info = this.props.result.data
    console.log(weather_info.weather[0].main) //天気は？
    console.log(weather_info.main.humidity) //湿度は？
    console.log(weather_info.wind.speed)  //風速は？
  }

  render() {
    const { handleSubmit, pristine, submitting } = this.props //redux-formからpropsを受け取る
    return (
      <form onSubmit={handleSubmit(this.onSubmit)}>
        <div><Field label="City" name="city" type="text" component={this.renderField} /></div>
        <div>
          <input type="submit" value="Submit" disabled={pristine || submitting} />
          <Link to="/" >Cancel</Link>
        </div>
      </form>
    );
  }
}

const validate = values => {
  const errors = {}

  if (!values.title) errors.title = "Enter a title, please."
  if (!values.body) errors.body = "Enter a body, please."
  return errors
}

const mapStateToProps = state => ({ result: state.result })
const mapDispatchToProps = ({ getInfo })

export default connect(mapStateToProps, mapDispatchToProps)(
  reduxForm({ validate, form: 'eventNewForm' })(EventsNew)
)
