import _ from 'lodash'
import {
    READ_EVENTS,
    READ_EVENT,
    DELETE_EVENT,
} from '../actions'

function reader (events = {}, action) {
    switch (action.type) {
        case READ_EVENT:
            const data = action.response.data
            return { ...events, [data.id]: data }
        case READ_EVENTS:
            return _.mapKeys(action.response.data, 'id') //reducerで受け取ったaction.response.dataのkeyにidを設定
        case DELETE_EVENT:
            delete events[action.id]
            return { ...events }
        default:
            return events
    }
}

export default reader;
