import {
    GET_INFO_REQUEST,
    GET_INFO_SUCCESS,
    GET_INFO_FAILURE
} from '../actions'

const initialState = {
    isFetching: false,
    data: []
}

const getInfo = (state = initialState, action) => {
    switch (action.type) {
        case GET_INFO_REQUEST:
            return (
                console.log('fetching...'),
                Object.assign({}, state, {
                isFetching: true,
                data: [],
            }));
        case GET_INFO_SUCCESS:
            return (
                console.log('Success!'),
                Object.assign({}, state, {
                isFetching: false,
                data: action.data
            }));
        case GET_INFO_FAILURE:
            return (
                console.log('Failure!'),
                Object.assign({}, state, {
                isFetching: false,
                error: action.error
            }));
        default:
            return state
    }
}

export default getInfo;
